import React, { useEffect, useState } from 'react';
import testRemoveBackground from './testRemoveBg';

/**
 * 測試頁面組件
 * 用於測試 remove.bg API 功能
 */
const TestPage = () => {
  const [isLoading, setIsLoading] = useState(false);
  const [result, setResult] = useState(null);
  const [error, setError] = useState(null);

  // 運行測試
  const runTest = async () => {
    setIsLoading(true);
    setError(null);
    setResult(null);

    try {
      const testResult = await testRemoveBackground();
      setResult(testResult);
    } catch (err) {
      console.error('測試失敗:', err);
      setError(err.toString());
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <div className="p-6 max-w-4xl mx-auto">
      <h1 className="text-2xl font-bold mb-4">Remove.bg API 測試頁面</h1>

      <button 
        className="px-4 py-2 bg-blue-500 text-white rounded hover:bg-blue-600 mb-6"
        onClick={runTest}
        disabled={isLoading}
      >
        {isLoading ? '測試中...' : '運行 API 測試'}
      </button>

      {isLoading && (
        <div className="mb-4 p-4 bg-gray-100 rounded">
          <p>正在測試 API，請稍候...</p>
        </div>
      )}

      {error && (
        <div className="mb-4 p-4 bg-red-100 border border-red-300 rounded">
          <h2 className="text-xl font-bold text-red-700 mb-2">測試失敗</h2>
          <pre className="whitespace-pre-wrap bg-white p-3 rounded border border-red-200">
            {error}
          </pre>
        </div>
      )}

      {result && (
        <div className="mb-4 p-4 bg-green-100 border border-green-300 rounded">
          <h2 className="text-xl font-bold text-green-700 mb-2">測試成功</h2>
          <p>去背成功！結果是一個 base64 字符串，長度為: {result.length}</p>

          <div className="mt-4 grid grid-cols-2 gap-4">
            <div>
              <h3 className="text-lg font-semibold mb-2">原始圖片:</h3>
              <img 
                src="https://images.unsplash.com/photo-1575936123452-b67c3203c357?q=80&w=1000&auto=format&fit=crop" 
                alt="原始圖片" 
                className="max-w-full border border-gray-300"
              />
            </div>
            <div>
              <h3 className="text-lg font-semibold mb-2">去背後的圖片:</h3>
              <img 
                src={result} 
                alt="去背後的圖片" 
                className="max-w-full border border-gray-300"
              />
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default TestPage;
