import React from 'react';

/**
 * ÂÅ¥ÈÇäÊ¨ÑÁµÑ‰ª∂
 * @param {Object} props - ÁµÑ‰ª∂Â±¨ÊÄß
 * @param {string} props.activePage - Áï∂ÂâçÊ¥ªÂãïÈ†ÅÈù¢ ('product', 'my', 'history', 'disclaimer')
 */
const Sidebar = ({ activePage }) => {
  // ÂÅ¥ÈÇäÊ¨ÑÈÅ∏È†Ö
  const sidebarOptions = [
    { id: 'product', label: 'ÂïÜÂìÅÂúñ', icon: 'üì∑' },
    { id: 'my', label: 'ÊàëÁöÑ', icon: 'üìÅ' },
    { id: 'history', label: 'ÁîüÊàêÁ¥ÄÈåÑ', icon: 'üìú' },
    { id: 'disclaimer', label: 'ÂÖçË≤¨ËÅ≤Êòé', icon: 'üìù' }
  ];

  return (
    <div className="w-16 bg-amber-50 border-r border-amber-100 flex flex-col items-center py-4">
      {sidebarOptions.map(option => (
        <button 
          key={option.id}
          className={`w-10 h-10 rounded flex flex-col items-center justify-center mb-6 ${
            activePage === option.id ? 'bg-amber-200' : 'hover:bg-amber-100'
          }`}
          title={option.label}
        >
          <span className="text-lg">{option.icon}</span>
          <span className="text-xs mt-1">{option.label}</span>
        </button>
      ))}
    </div>
  );
};

export default Sidebar;
